import React, { useState, useEffect } from "react";
import "../scss/_Main.scss";
import TakeQuizQuestion from "./TakeQuizQuestion";
import TakeQuizButtons from "./TakeQuizButtons";
import TakeQuizCount from "./TakeQuizCount";
import TakeQuizSummary from "./TakeQuizSummary";

function TakeQuiz({
  correctCounterHandler,
  setCorrectCounter,
  correctCounter,
  quizQuestions
}) {
  const [innerQuizQuestions, setinnerinnerQuizQuestions] = useState();
  const [questionNumber, setQuestionNumber] = useState(0);

  useEffect(() => {
    // fetchAllQuizes();
    setInnerQuizQuestions
  }, []);

  const clickHandler = (e) => {
    correctCounterHandler(e);
    if (innerQuizQuestions.questions.length > questionNumber) {
      setQuestionNumber((prev) => prev + 1);
    } else {
      return;
    }
  };
  const startOver = () => {
    setQuestionNumber(0);
    setCorrectCounter(0);
    // setinnerQuizQuestions((prev) => shuffle(prev));
  };

  function shuffle(array) {
    var m = array.length,
      t,
      i;

    // While there remain elements to shuffle…
    while (m) {
      // Pick a remaining element…
      i = Math.floor(Math.random() * m--);

      // And swap it with the current element.
      t = array[m];
      array[m] = array[i];
      array[i] = t;
    }

    return array;
  }

  const shuffleQuestions = (obj) => {
    // shuffle(obj.questions)
    obj.questions.forEach((arr) => shuffle(arr[0].answers));
  };

  // const fetchAllQuizes = () => {
  //   fetch("http://localhost:4000/quizs")
  //     .then((resp) => resp.json())
  //     .then((data) =>
  //       data.forEach((e) => {
  //         if (e.id === quizID) {
  //           setinnerQuizQuestions(e);
  //         }
  //       })
  //     );
  // };
  // console.log(innerQuizQuestions)
  if (innerQuizQuestions) {
    shuffleQuestions(innerQuizQuestions);
  }
  return (
    <div className="takeQuizContent">
      {innerQuizQuestions && questionNumber === innerQuizQuestions.questions.length ? (
        <>
          <TakeQuizButtons startOver={startOver} />
          <TakeQuizSummary
            correctCounter={correctCounter}
            innerQuizQuestions={innerQuizQuestions}
          />
        </>
      ) : (
        <>
          <TakeQuizButtons startOver={startOver} />
          <TakeQuizQuestion
            questionNumber={questionNumber}
            clickHandler={clickHandler}
            quizQuestions={innerQuizQuestions}
          />
          <TakeQuizCount
            questionNumber={questionNumber}
            quizQuestions={innerQuizQuestions}
          />
        </>
      )}
    </div>
  );
}

export default TakeQuiz;
