import React, { useState, useEffect } from "react";
import "../scss/_Main.scss";
import FirstPage from "./FirstPage";
import NewQuiz from "./NewQuiz";
import TakeQuiz from "./TakeQuiz";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
function MainView() {
  useEffect(() => {
    fetchAllQuizes();
  }, []);
  const [quizQuestions, setQuizQuestions] = useState();
  const [quizID, setQuizID] = useState(1);
  const [correctCounter, setCorrectCounter] = useState(0);
//  console.log(quizQuestions)
  const fetchAllQuizes = () => {
    fetch("http://localhost:4000/quizs")
      .then((resp) => resp.json())
      .then((data) =>{
       const currentQuiz = data.find((item)=>item.id == quizID)
       console.log(currentQuiz)
       setQuizQuestions(currentQuiz)
        // data.find((item) => {
        //   if (item.id.tostring == quizID) {
        //     setQuizQuestions(e);
        //   }
        }
      );
  };

  const correctCounterHandler = (e) => {
    if (e.target.dataset.correct === "true") {
      setCorrectCounter((prev) => prev + 1);
    }
  };
  const chooseQuizHandler = (event) => {
    console.log(event.target.id)
    setQuizID(event.target.id);
    fetchAllQuizes()
  };
  return (
    <Router>
      {/* {console.log(quizQuestions)} */}
      <div className="appBody">
        <header className="header">
          <div className="logo">GetGood</div>
        </header>
        <Switch>
          <Route
            path="/"
            exact
            render={() => <FirstPage chooseQuizHandler={chooseQuizHandler} />}
          />
          <Route path="/newquiz" component={NewQuiz} />
          <Route
            path="/takequiz"
            render={() => (
              <TakeQuiz
                setCorrectCounter={setCorrectCounter}
                correctCounterHandler={correctCounterHandler}
                correctCounter={correctCounter}
                quizQuestions={quizQuestions}
                quizID={quizID}
              />
            )}
          />
        </Switch>
      </div>
    </Router>
  );
}

export default MainView;
