import React, { useState } from "react";
import "../scss/_NewQuiz.scss";
import NewQuizButtons from "./NewQuizButtons";
import NewQuizinput from "./NewQuizInput";
import NewQuizScrolbar from "./NewQuizScrolbar";

function NewQuiz() {
  const [elementData, setElementData] = useState({
    title: "",
    question: "",
    correctAnswer: "",
    answer1: "",
    answer2: "",
    answer3: "",
  });

  const [quizData, setQuizData] = useState([]);

  let apiObject;

  if(quizData.length > 0){
    apiObject = {
      name: quizData[0].title,
      questions: quizData.map((e) => [
        {
          content: e.question,
          answers: [
            {
              content: e.correctAnswer,
              correct: true,
            },
            {
              content: e.answer1,
              correct: false,
            },
            {
              content: e.answer2,
              correct: false,
            },
            {
              content: e.answer3,
              correct: false,
            },
          ],
        },
      ]),
    };
  }


  const quizSubmit = () => {
    setQuizData((prev) => [...prev, elementData]);
    fetch(`http://localhost:4000/quizs`, {
      method: "POST",
      body: JSON.stringify(apiObject),
      headers: {
        "Content-Type": "application/json",
      },
    });
  };

  const changeHandler = ({ target: { name, value } }) => {
    setElementData((prev) => ({ ...prev, [name]: value }));
  };

  const newQuestionHandler = () => {
    if(elementData.title === '' || quizData[0].title && elementData.question === '' && elementData.correctAnswer === '' && elementData.answer1 === '' && elementData.answer2 === '' && elementData.answer3 === ''){
      alert('Wypełnij puste pola');
      return
    }
    setQuizData((prev) => [...prev, elementData]);
    setElementData({
      title: "",
      question: "",
      correctAnswer: "",
      answer1: "",
      answer2: "",
      answer3: "",
    });
    console.log(apiObject)
  };
  return (
    <div className="newQuizContent">
      <NewQuizButtons
        quizSubmit={quizSubmit}
        newQuestionHandler={newQuestionHandler}
      />
      <NewQuizinput quizData={quizData} elementData={elementData} changeHandler={changeHandler} />
      <NewQuizScrolbar quizData={quizData} />
    </div>
  );
}

export default NewQuiz;
