import React from "react";
import "../scss/_TakeQuizQuestion.scss";

function TakeQuizQuestion({ quizQuestions,submitHandler,questionNumber }) {

  return (
    <div className="takeQuizQuestion">
      {quizQuestions && (
        <p className="takeQuizQuestionContent">
          {quizQuestions.questions[questionNumber][0].content}
        </p>
      )}
      {quizQuestions && (
        <form onSubmit={submitHandler} className="takeQuizAnswers">
          <button iscorrect={quizQuestions.questions[questionNumber][0].answers[0].correct} className="takeQuizAnswer">{quizQuestions.questions[questionNumber][0].answers[0].content}</button>
          <button iscorrect={quizQuestions.questions[questionNumber][0].answers[0].correct} className="takeQuizAnswer">{quizQuestions.questions[questionNumber][0].answers[1].content}</button>
          <button iscorrect={quizQuestions.questions[questionNumber][0].answers[0].correct} className="takeQuizAnswer">{quizQuestions.questions[questionNumber][0].answers[2].content}</button>
          <button iscorrect={quizQuestions.questions[questionNumber][0].answers[0].correct} className="takeQuizAnswer">{quizQuestions.questions[questionNumber][0].answers[3].content}</button>
        </form>
      )}
    </div>
  );
}

export default TakeQuizQuestion;
