import React, { useState, useEffect } from "react";
import "../scss/_TakeQuiz.scss";
import TakeQuizQuestion from "./TakeQuizQuestion";
import TakeQuizButtons from "./TakeQuizButtons";
import TakeQuizCount from "./TakeQuizCount";

function TakeQuiz({ quizID }) {
  useEffect(() => {
    fetchAllQuizes();
  }, []);

  const [quizQuestions, setQuizQuestions] = useState([]);
  const [questionMaxCount, setQuestionMaxCount] = useState('')

  if(quizQuestions.questions && quizQuestions.questions.length === questionMaxCount){
      setQuestionMaxCount(quizQuestions.questions.lenght)
  }

  console.log(questionMaxCount)

  const fetchAllQuizes = () => {
    fetch("http://localhost:4000/quizs")
      .then((resp) => resp.json())
      .then((data) =>
        data.forEach((e) => {
          if (e.id === quizID) {
            return setQuizQuestions(e);
          } else {
            return;
          }
        })
      );
  };

  return (
    <div className="takeQuizContent">
      <TakeQuizButtons />
      <TakeQuizQuestion />
      <TakeQuizCount  />
    </div>
  );
}

export default TakeQuiz;
