import React, { useState } from "react";
import "../scss/_NewQuizInput.scss";

function NewQuizInput() {
  const [element, setElement] = useState({
    title: "",
    question: "",
    correctAnswer: "",
    answer1: "",
    answer2: "",
    answer3: "",
  });

  const changHandler = ({ target: { name, value } }) => {
    setInputData((prev) => ({ ...prev, [name]: value }));
  };
  return (
    <form className="newQuizForm">
      <label>
        Tytuł:
        <input type="text" name="title" value={element.title} />
      </label>
      <label>
        Treść pytania:
        <input type="text" name="question" value={element.question} />
      </label>
      <label>
        Poprawna odpowiedź:
        <input type="text" name="correctAnswer" value={element.correctAnswer} />
      </label>
      <label>
        Pierwsza odpowiedź:
        <input type="text" name="answer1" value={element.answer1} />
      </label>
      <label>
        Druga odpowiedź:
        <input type="text" name="answer2" value={element.answer2} />
      </label>
      <label>
        Trzecia odpowiedź:
        <input type="text" name="answer3" value={element.answer3} />
      </label>
    </form>
  );
}

export default NewQuizInput;
